class {
  constructor(hostRef) {
    registerInstance(this, hostRef);
    this.first = undefined;
    this.middle = undefined;
    this.last = undefined;
  }
  getText() {
    return format(this.first, this.middle, this.last);
  }
  render() {
    return h("div", null, "Hello, World! I'm ", this.getText());
  }
}

class {
  constructor(hostRef) {
    registerInstance(this, hostRef);
    this.size = 'm';
    this.color = undefined;
    this.weight = undefined;
    this.maxLines = undefined;
    this.loaded = undefined;
  }
  componentDidLoad() {
    // Set loaded to true so colors are correct
    requestAnimationFrame(() => {
      this.loaded = true;
    });
  }
  render() {
    const style = {};
    if (this.maxLines != null) {
      style['--max-lines'] = this.maxLines;
    }
    if (this.color) {
      style['--text-color'] = `var(--color-${this.color})`;
    }
    return (h(Host, { style: style, class: this.loaded ? 'loaded' : '' }, h("span", null, h("slot", null))));
  }
}

class {
  constructor(hostRef) {
    registerInstance(this, hostRef);
    this.weight = 'regular';
    this.color = undefined;
    this.maxLines = undefined;
  }
  render() {
    return (h("true-text-base", { size: "m", weight: this.weight, maxLines: this.maxLines, color: this.color }, h("slot", null)));
  }
}

